import React from 'react'
import styles from './styles.module.css'

export default function Modal(
    {
   show=false,
   opacity = 0.5
    }
){
    return (
        <div className={show ? styles.modalshow : styles.modalhide}>
            <div className={styles.modalbody}>
                Model component
               
            </div>
        </div>
       
    )
}
